package com.piersoft.converse.vendor.persistence.model;


import com.amazonaws.services.dynamodbv2.datamodeling.*;
import lombok.AllArgsConstructor;
import lombok.Builder;
import lombok.Data;
import lombok.NoArgsConstructor;

import java.time.LocalDateTime;
import java.time.ZonedDateTime;

@Data
@Builder
@NoArgsConstructor
@AllArgsConstructor
@DynamoDBTable(tableName = "gatepass")
public class GatepassDO {

    @DynamoDBHashKey
    @DynamoDBAutoGeneratedKey
    private String id;
    private String projectId;
    private String driverName;
    private String driverPhoneNumber;
    private String material;
    private String vehicleNo;
    private String vehicleImgUrl;
    private String poNumber;
    private String poNumberImgUrl;
    @DynamoDBTypeConverted(converter = LocalDateTimeConverter.class)
    private ZonedDateTime createdDateTime;
    @DynamoDBTypeConverted(converter = LocalDateTimeConverter.class)
    private ZonedDateTime lastUpdatedTime;
    private String status;

    static public class LocalDateTimeConverter implements DynamoDBTypeConverter<String, ZonedDateTime> {
        @Override
        public String convert(ZonedDateTime time) {
            return time.toString();
        }

        @Override
        public ZonedDateTime unconvert(String object) {
            return ZonedDateTime.parse(object);
        }
    }
}


